function TerrainKindId2TerrainType(terrainKindId)
    if(terrainKindId == 1)then
        alias = "s"
    elseif(terrainKindId == 2)then
        alias = "d"
    elseif(terrainKindId == 3)then
        alias = "w"
    elseif(terrainKindId == 4)then
        alias = "w2"
    else
        return nil
    end

    return MEC_core.getTerrainTypes():getByLabel(alias):getTerrainTypeId()
end

